//     type: 'registrar_mensaje',
//   })
// }

export const registrarMensaje = (colegio) => async (dispatch) => {
  let mensajes=[];
  if(colegio.codigoweb!==undefined){
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
    let data = {
      plantel:this.props.colegio.codigo,
      grado: this.props.singleSelect,
      seccion:this.props.seccion.value,
      titulo:this.props.titulo,
      fecha:this.props.fecha,
      mensaje:this.props.mensaje,
    };
    let respuesta = await fetch(url, {
        method: 'POST',
        body: JSON.stringify(data),
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    mensajes = await respuesta.json();
  }else{
    mensajes=[]
  }
  dispatch({
    type: 'registrar_mensaje',
    payload: mensajes
  });
}

export const verAlumnos = (id,grado) => async (dispatch) => {
  if(grado!="" || grado!==undefined){
    grado=grado.substr(3,6).toUpperCase();
  }
  let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/veralumnos?id='+id+'&grado='+grado;
  let respuesta = await fetch(url, {
      method: 'GET',
      headers:{
          'Content-Type': 'application/json'
      },
      Accept: 'application/json',
  })
  .catch(error => {
      console.log(error);
  });
  let result = await respuesta.json();
  dispatch({
    type: 'ver_alumnos',
    payload: result
  });
}

export const verCredenciales =  () => async (dispatch) => {
    const url = 'https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/creds';
    let respuesta = await fetch(url, {
        method: 'POST',
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let insercion = await respuesta.json();
    dispatch({
        type: 'ver_credenciales',
        payload: insercion
    });
  }
