  constructor() {
    super();
    this.state = {
      singleSelect: null,
      tareas: [],
      mensaje: null,
      subiendo: null,
      materias:null,
      seccion:null,
      asignatura:null,
      file: null,
      imagePreviewUrl: defaultImage,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
      subirRecurso:null,
      envios:[],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
  }
  handleClick(e) {
  }
  handleRemove() {
    this.setState({
      file: null,
      imagePreviewUrl: this.props.avatar ? defaultAvatar : defaultImage,
    });
    this.refs.fileInput.value = null;
  }
  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }
  setSeccion = (value) => {
    this.setState({ seccion: value })
  }

  componentDidMount = async () => {
    this.props.verCredenciales();
    this.props.verColegio()

    if(!this.props.colegio.id){
      this.props.history.push("/auth/login");
    }
    await this.verMensaje(this.props.colegio.id,"*");
  }

  verMensaje = async (id,grado) => {
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/micolegio/incoming_webhook/leerMensajes'
    +id+"&grado="+grado;
    let respuesta = await fetch(url, {
        method: 'GET',
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let mensajes = await respuesta.json();
    this.setState({ mensajes:mensajes});
  }

  RegistrarMensaje = async (objectid, valor) =>{
    const data = {
      id:objectid,
      valor: valor
    };
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
    let respuesta = await fetch(url, {
        method: 'POST', 
        body: JSON.stringify(data),
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    })
    .then(async resp  => {
      let nube = (
        <Alert color="success">
          <span>Mensaje en la nube</span>
        </Alert>
      )
      this.setState({
        subiendo:nube,
        seccion:null,
        singleSelect:null
      });
      await this.verMensaje(this.props.colegio.id);
    })
    .catch(error => {
        console.log(error);
    });
    let result = await respuesta.json();
  }

