import React from "react";

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Label,
  FormGroup,
  Input,
  Alert,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
} from "reactstrap";
import ReactBSAlert from "react-bootstrap-sweetalert";
import ReactDatetime from "react-datetime";
import defaultImage from "assets/img/image_placeholder.jpg";
import defaultAvatar from "assets/img/placeholder.jpg";
import Select from "react-select";
import * as mainActions from "../actions/mainActions";
import { connect } from 'react-redux';
import { uploadFile } from 'react-s3';

class Mensajes extends React.Component {
  constructor() {
    super();
    this.state = {
      singleSelect: null,
      tareas: [],
      subiendo: null,
      materias:null,
      seccion:null,
      asignatura:null,
      file: null,
      imagePreviewUrl: defaultImage,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
      subirRecurso:null,
      mensaje:null,
      enviadas:[],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
  }
  handleClick(e) {
  }
  handleRemove() {
    this.setState({
      file: null,
      imagePreviewUrl: this.props.avatar ? defaultAvatar : defaultImage,
    });
    this.refs.fileInput.value = null;
  }
  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }
  setSeccion = (value) => {
    this.setState({ seccion: value })
  }
  // componentDidMount = async () => {
  //   this.props.verCredenciales();
  //   if(!this.props.colegio.id){
  //     this.props.history.push("/auth/login");
  //   }
  //   await this.verTarea(this.props.colegio.id,"*");
  // }

  RegistrarMensaje = async (objectid, mensaje) =>{
    const data = {
      id:objectid,
      mensaje: mensaje
    };
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
    let respuesta = await fetch(url, {
        method: 'POST',
        body: JSON.stringify(data),
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    })
    .catch(error => {
        console.log(error);
    });
    let result = await respuesta.json();
    this.setState({mensaje:mensaje});
  }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form className="form-horizontal">
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
                          options={this.props.Seccion}
                          placeholder="Seccion"
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Contenido del Mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          {/* <Input placeholder="Asunto del mensaje"
                            type="text"
                            value={this.state.mensaje}
                            onChange={(texto) =>
                              this.setState({ mensaje:texto.target.value })
                            }
                          /> */}
                          <Input placeholder="Asunto del mensaje"
                            type="text"
                            value={this.state.mensaje}
                            onChange={(texto) =>
                              this.setState({ mensaje:texto.target.value })
                            }
                          />
                        </FormGroup>
                      </Col>
                      {this.state.mensaje ? (
                        <Row>
                          <Col md="3" />
                          <Col md="9">
                            <Button className="btn-round"
                            color="info" type="submit"
                            onClick={()=>this.RegistrarMensaje()}
                          >
                              Subir y registrar mensaje
                            </Button>
                          </Col>
                        </Row>
                      ):(null)}
                    </Row>
                  </Form>
                </CardBody>
              </Card>
            </Col>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardText tag="div">
                    <CardTitle tag="h4">Mensajes Enviados</CardTitle>
                  </CardText>
                </CardHeader>
                <CardBody className="table-responsive">
                  <Table className="table-hover">
                    <thead className="text-warning">
                      <tr>
                        <th>#</th>
                        <th>Grado</th>
                        <th>Asunto</th>
                        <th>Mensaje</th>
                      </tr>
                    </thead>
                    <tbody>
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Row>
            <Col className="text-center" lg="6" md="6">
              <Row>
              <Col className="text-center" lg="12" md="12">
              <Card className="card-tasks">
                <CardHeader>
                  <Row>
                  <Col md="6">
                    <CardTitle tag="h4">
                      Mensajes Enviados
