import React from "react";

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Label,
  FormGroup,
  Input,
  Alert,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
} from "reactstrap";
import ReactBSAlert from "react-bootstrap-sweetalert";
import ReactDatetime from "react-datetime";
import defaultImage from "assets/img/image_placeholder.jpg";
import defaultAvatar from "assets/img/placeholder.jpg";
import Select from "react-select";
import * as mainActions from "../actions/mainActions";
import { connect } from 'react-redux';
import { uploadFile } from 'react-s3';

class Mensajes extends React.Component {
  constructor() {
    super();
    this.state = {
      singleSelect: null,
      subiendo: null,
      seccion:null,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
      subirRecurso:null,
      mensaje:null,
      mensajes:[],
      registrados:[],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
  }
  handleClick(e) {
    this.refs.fileInput.click();
  }
  handleRemove() {
    this.setState({
      file: null,
      imagePreviewUrl: this.props.avatar ? defaultAvatar : defaultImage,
    });
    this.refs.fileInput.value = null;
  }
  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }
  setSeccion = (value) => {
    this.setState({ seccion: value })
  }

  verMensaje = async () => {
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/micolegio/incoming_webhook/leerMensajes'
    let respuesta = await fetch(url, {
        method: 'GET',
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let mensajes = await respuesta.json();
    this.setState({ mensajes:mensajes});
    console.log(mensajes)
  }


  componentDidMount = async () => {
    this.props.verCredenciales();
    if(!this.props.colegio.id){
      this.props.history.push("/auth/login");
    }
    await this.verMensaje(this.props.colegio.id,"*");
  }

// registrarMensaje = async () => {
//   let mensaje = this.state.mensaje;
//   if (mensaje) {
//     let subiendo = (
//       <Alert color="info">
//         <span>Subiendo Mensaje</span>
//       </Alert>
//     );
//     this.setState({subiendo:subiendo});
//     const datadir = {
//       plantel:this.props.colegio.codigo,
//       grado: this.state.singleSelect,
//       seccion:this.state.seccion,
//       titulo:this.state.mensaje.titulo,
//       fecha:this.state.fecha,
//       mensaje:this.state.mensaje,
//     };
//     let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
//     fetch(url, {
//       method: 'POST',
//       body: JSON.stringify(datadir),
//       headers:{
//         'Content-Type': 'application/json'
//       },
//     Accept: 'application/json',
//   });
//   this.setState({subiendo:nube,
//     seccion:null, singleSelect:null});
//     await this.verMensaje(this.props.colegio.id);
//     this.setState({subirRecurso:false, mensaje:null
//   });
// };

async registrarMensaje() {
  let mensaje = this.state.mensaje;
  let codColegio = this.props.colegio.codigo;
  if (mensaje) {
      let subiendo = (
        <Alert color="info">
          <span>Subiendo Mensaje</span>
        </Alert>
      );
      this.setState({subiendo:subiendo});
      await  uploadFile(mensaje, {
          bucketName: 'pruebareact',
          dirName: 'aprende/'+codColegio,
          region: 'us-east-1',
          accessKeyId: this.props.as3,
          secretAccessKey: this.props.ss3,
      })
      .then(data => {
          const datadir = {
            plantel:this.props.colegio.codigo,
            grado: this.state.singleSelect,
            seccion:this.state.seccion,
            titulo:this.state.mensaje.titulo,
            fecha:this.state.fecha,
            mensaje:this.state.mensaje,
          };
          let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
          fetch(url, {
              method: 'POST',
              body: JSON.stringify(datadir),
              headers:{
                  'Content-Type': 'application/json'
              },
              Accept: 'application/json',
          })
          .then(async resp  => {
            let nube = (
              <Alert color="success">
                <span>Mensaje en la nube</span>
              </Alert>
            )
            this.setState({subiendo:nube,
              seccion:null, singleSelect:null});
              await this.verMensaje(this.props.colegio.id);
              this.setState({subirRecurso:false, mensaje:null
            });
          })
          .catch(error => {
            console.log(error);
        });
    })
    .catch(err => console.error(err));
  }
}

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form className="form-horizontal" >
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
                          options={this.props.Seccion}
                          placeholder="Seccion"
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Titulo del mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          <Input placeholder="Nombre descriptivo del mensaje"
                            type="text"
                            value={this.state.titulo}
                            onChange={(texto) =>
                                this.setState({ titulo:texto.target.value })
                              }
                            />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Contenido del Mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          <Input placeholder="Asunto del mensaje"
                            type="text"
                            value={this.state.mensaje}
                            onChange={(texto) =>
                              this.setState({ mensaje:texto.target.value })
                            }
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter>
                  {this.state.mensaje ? (
                    <Row>
                      <Col md="3" />
                      <Col md="9">
                        <Button className="btn-round"
                        color="info" type="button"
                        onClick={()=>this.registrarMensaje()}
                      >
                          Registrar mensaje
                        </Button>
                      </Col>
                    </Row>
                    ):(null)}
                    {/* {this.state.mensajes !== null ? (
                      <Col md="9">
                        <Button className="btn-round"
                        color="info" type="submit"
                        onClick={()=>this.registrarMensaje()}
                      >
                        Registrar mensaje
                      </Button>
                    </Col> ) : (null)} */}
                  </CardFooter>
              </Card>
            </Col>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardText tag="div">
                    <CardTitle tag="h4">Mensajes Enviados</CardTitle>
                  </CardText>
                </CardHeader>
                <CardBody className="table-responsive">
                  <Table className="table-hover">
                    <thead className="text-warning">
                      <tr>
                        <th>#</th>
                        <th>Grado</th>
                        <th>Asunto</th>
                        <th>Mensaje</th>
                      </tr>
                    </thead>
                    <tbody>
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Row>
            <Col className="text-center" lg="6" md="6">
              <Row>
              <Col className="text-center" lg="12" md="12">
              <Card className="card-tasks">
                <CardHeader>
                  <Row>
                  <Col md="6">
                    <CardTitle tag="h4">
                      Mensajes Enviados
                    </CardTitle>
                  </Col>
                  <Col md="6">
                    <Select
                      className="react-select primary"
                      classNamePrefix="react-select"
                      name="singleSelect"
                      value={this.state.parabuscar}
                      onChange={(value) =>
                        this.verMensaje(this.props.colegio.id,value.key)
                      }
                      options={this.props.Grado}
                      placeholder="Seleccion el Grado o año"
                    />
                  </Col>
                  </Row>
                </CardHeader>
                <CardBody>
                  <div className="table-full-width table-responsive">
                    <Table>
                      <tbody>
                        {this.state.mensajes.map(mensaje=>(
                          <tr>
                            <td >
                              <div className="timeline-badge danger">
                                <i className="nc-icon nc-single-copy-04" />
                              </div>
                            </td>
                            <td className="text-left">
                              {mensaje.grado} {mensaje.seccion}
                            </td>
                            <td className="text-left">
                              {mensaje.titulo} {mensaje.mensaje}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </div>
                </CardBody>
                <CardFooter>
                  <hr />
                  <div className="stats">
                    <i className="fa fa-refresh spin" />
                    Updated 3 minutes ago
                  </div>
                </CardFooter>
              </Card>
            </Col>
            </Row>
            </Col>
          </Row>
          <Row>
          </Row>
        </div>
      </>
    );
  }
}

const mapStateToProps = (reducers) => {
  return reducers.mainReducer;
}

export default connect(mapStateToProps, mainActions )(Mensajes);
