import React from "react";

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Label,
  FormGroup,
  Input,
  Alert, 
  Table,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
} from "reactstrap";
import ReactBSAlert from "react-bootstrap-sweetalert";
import ReactDatetime from "react-datetime";
import defaultImage from "assets/img/image_placeholder.jpg";
import defaultAvatar from "assets/img/placeholder.jpg";
import Select from "react-select";
import * as mainActions from "../actions/mainActions";
import { connect } from 'react-redux';
import { uploadFile } from 'react-s3';

class Mensajes extends React.Component {
  constructor() {
    super();
    this.state = {
      singleSelect: null,
      tareas: [],
      value: '',
      mensajes:[],
      subiendo: null,
      materias:null,
      seccion:null,
      asignatura:null,
      file: null,
      imagePreviewUrl: defaultImage,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    // this.handleChange = this.handleChange.bind(this);
    // this.submitMessages = this.submitMessages.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();
    alert('Un mensaje se mandara: ' + this.state.value);
    e.preventDefault();
  }

  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }

  setSeccion = (value) => {
    this.setState({ seccion: value })
  }

  setModulos = (value)  => {
    this.setState({ modulo: value })
  }

  verMensajes = async (id,grado) => {
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/verRecursos?id='
      +id+"&grado="+grado;
    let respuesta = await fetch(url, {
        method: 'GET',
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let mensajes = await respuesta.json();
    this.setState({ mensajes:mensajes});
  }

  quitarMensaje = async (id) => {
    const datadir = {
      codigo:id,
    };
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/quitarRecurso';
    fetch(url, {
        method: 'POST',
        body: JSON.stringify(datadir),
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    })
    .then(async resp  => {
        await this.verMensajes(this.props.colegio.id);
    })
    .catch(error => {
        console.log(error);
    });
  }

  async verModulos(){
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/vermodulos';
    let respuesta = await fetch(url, {
      method: 'GET',
      mode: 'cors',
      cache: 'default',
      headers:{
          'Content-Type': 'application/json'
      },
      Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let modulos = await respuesta.json();

    this.setState({ modulos:modulos});
  }

  componentDidMount = async () => {
    this.props.verCredenciales();
    if(!this.props.colegio.id){
      this.props.history.push("/auth/login");
    }
    await this.verMensajes(this.props.colegio.id,"*");
  }

  async uploadFile() {
    let mensajes = this.state.mensajes;
    let codColegio = this.props.colegio.codigo;
    if (mensajes) {
      let subiendo = (
        <Alert color="info">
          <span>Subiendo Mensaje</span>
        </Alert>
      )
        this.setState({subiendo:subiendo});
        await  uploadFile(mensajes, {
            bucketName: 'pruebareact',
            dirName: 'aprende/'+codColegio,
            region: 'us-east-1',
            accessKeyId: this.props.as3,
            secretAccessKey: this.props.ss3,
        })
        .then(data => {
            const datadir = {
                direccion:data.location,
                plantel:this.props.colegio.codigo,
                materia: this.state.asignatura,
                grado: this.state.singleSelect,
                seccion:this.state.seccion.value,
                titulo:this.state.titulo,
                fecha:this.state.fecha,
                size:this.state.file.size,
                tipo:this.state.file.type,
            };
            let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/guardarRecurso';
            fetch(url, {
                method: 'POST',
                body: JSON.stringify(datadir),
                headers:{
                    'Content-Type': 'application/json'
                },
                Accept: 'application/json',
            })
            .then(async resp  => {
                let nube = (
                  <Alert color="success">
                    <span>Mensaje en la nube</span>
                  </Alert>
                )
                this.setState({subiendo:nube,
                asignatura:null, singleSelect:null});
                await this.verMensajes(this.props.colegio.id);
            })
            .catch(error => {
                console.log(error);
            });
        })
        .catch(err => console.error(err));
    }
  }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="6" lg="6" justify="center">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form className="form-horizontal">
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
                          options={this.props.Seccion}
                          placeholder="Seccion"
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Contenido del Mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          <Input placeholder="Nombre descriptivo de la tarea"
                            type="text"
                            value={this.state.value}
                            onChange={this.handleChange}
                            // value={this.state.titulo}
                            // onChange={(texto) =>
                            //   this.setState({ titulo:texto.target.value })
                            // }
                            />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Vencimiento</Label>
                      <Col md="6">
                        <ReactDatetime
                          inputProps={{
                            className: "form-control",
                            placeholder: "Date Picker Here",
                          }}
                          timeFormat={false}
                          value={this.state.fecha}
                          onChange={(value) =>
                            this.setState({ fecha: value })
                          }
                        />
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter>
                  <Row>
                    <Col md="3" />
                      {/* {this.state.mensajes !== null ? ( */}
                      <Col md="9">
                        <Button className="btn-round"
                        color="info" type="submit"
                        onSubmit={this.handleSubmit}
                        // onClick={()=>this.uploadFile()}
                      >
                        Subir y registrar mensaje
                      </Button>
                    </Col>
                     {/* ) : (null)} */}
                  </Row>
                </CardFooter>
              </Card>
            </Col>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardText tag="div">
                    <CardTitle tag="h4">Mensajes Enviados</CardTitle>
                  </CardText>
                </CardHeader>
                <CardBody className="table-responsive">
                  <Table className="table-hover">
                    <thead className="text-warning">
                      <tr>
                        <th>#</th>
                        <th>Grado</th>
                        <th>Mensaje</th>
                      </tr>
                    </thead>
                    <tbody>
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Row>
            <Col className="text-center" lg="6" md="6">
              <Row>
                <Col className="text-center" lg="12" md="12">
                <Card className="card-tasks">
                  <CardHeader>
                    <Row>
                      <Col md="6">
                        <CardTitle tag="h4">
                          Mensajes Enviados
                        </CardTitle>
                      </Col>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect" 
                          value={this.state.parabuscar}
                          onChange={(value) =>
                            this.verMensajes(this.props.colegio.id,value.key)
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                    </Row>
                </CardHeader>
                <CardBody>
                  <div className="table-full-width table-responsive">
                    <Table>
                      <tbody>
                        {this.state.mensajes.map(mensaje => (
                          <tr>
                            <td >
                              <div className="timeline-badge danger">
                                <i className="nc-icon nc-single-copy-04" />
                              </div>
                            </td>
                            <td className="text-left">
                              {mensaje.grado} {mensaje.input}
                            </td>
                            {/* <td className="td-actions text-right">
                              <Button
                                className="btn-round btn-icon btn-icon-mini btn-neutral"
                                color="info"
                                title=""
                                type="button"
                                onClick={()=>this.ponerModal(mensaje._id)}
                              >
                                <i className="nc-icon nc-ruler-pencil" />
                              </Button>
                              <Button
                                className="btn-round btn-icon btn-icon-mini btn-neutral"
                                color="danger"
                                title=""
                                type="button"
                                onClick={()=>this.quitarTarea(mensaje._id)}
                              >
                                <i className="nc-icon nc-simple-remove" />
                              </Button>
                            </td> */}
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </div>
                </CardBody>
                <CardFooter>
                  <hr />
                  <div className="stats">
                    <i className="fa fa-refresh spin" />
                    Updated 3 minutes ago
                  </div>
                </CardFooter>
              </Card>
            </Col>
            </Row>
            </Col>
          </Row>
          <Row>
          </Row>
        </div>
      </>
    );
  }
}

const mapStateToProps = (reducers) => {
  return reducers.mainReducer;
}

export default connect( mapStateToProps, mainActions )(Mensajes);
