import React from "react";

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Label,
  FormGroup,
  Input,
  Alert, 
  Table,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
} from "reactstrap";
import ReactBSAlert from "react-bootstrap-sweetalert";
import ReactDatetime from "react-datetime";
import defaultImage from "assets/img/image_placeholder.jpg";
import defaultAvatar from "assets/img/placeholder.jpg";
import Select from "react-select";
import * as mainActions from "../actions/mainActions";
import { connect } from 'react-redux';
import { uploadFile } from 'react-s3';

class Mensajes extends React.Component {
  constructor() {
    super();
    this.state = {
      singleSelect: null,
      tareas: [],
      input: null,
      mensajes:[],
      subiendo: null,
      materias:null,
      seccion:null,
      asignatura:null,
      file: null,
      imagePreviewUrl: defaultImage,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    // this.handleChange = this.handleChange.bind(this);
    // this.submitMessages = this.submitMessages.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    // this.state.file is the file/image uploaded
    // in this function you can save the image (this.state.file) on form submit
    // you have to call it yourself
  }

  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }

  setSeccion = (value) => {
    this.setState({ seccion: value })
  }

  setModulos = (value)  => {
    this.setState({ modulo: value })
  }

  verMensajes = async (id,grado) => {
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/verRecursos?id='
      +id+"&grado="+grado;
    let respuesta = await fetch(url, {
        method: 'GET',
        mode: 'cors',
        cache: 'default',
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let mensajes = await respuesta.json();
    this.setState({ mensajes:mensajes});
  }

  quitarMensaje = async (id) => {
    const datadir = {
      codigo:id,
    };
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/quitarRecurso';
    fetch(url, {
        method: 'POST',
        body: JSON.stringify(datadir),
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    })
    .then(async resp  => {
        await this.verMensajes(this.props.colegio.id);
    })
    .catch(error => {
        console.log(error);
    });
  }

  async verModulos(){
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/vermodulos';
    let respuesta = await fetch(url, {
      method: 'GET',
      mode: 'cors',
      cache: 'default',
      headers:{
          'Content-Type': 'application/json'
      },
      Accept: 'application/json',
    }).catch(error => {
        console.log(error);
    });
    let modulos = await respuesta.json();

    this.setState({ modulos:modulos});
  }

  componentDidMount = async () => {
    this.props.verCredenciales();
    if(!this.props.colegio.id){
      this.props.history.push("/auth/login");
    }
    await this.verMensajes(this.props.colegio.id,"*");
  }

  async uploadFile() {
    let mensajes = this.state.mensajes;
    let codColegio = this.props.colegio.codigo;
    if (mensajes) {
      let subiendo = (
        <Alert color="info">
          <span>Subiendo Mensaje</span>
        </Alert>
      )
        this.setState({subiendo:subiendo});
        await  uploadFile(mensajes, {
            bucketName: 'pruebareact',
            dirName: 'aprende/'+codColegio,
            region: 'us-east-1',
            accessKeyId: this.props.as3,
            secretAccessKey: this.props.ss3,
        })
        .then(data => {
            const datadir = {
                direccion:data.location,
                plantel:this.props.colegio.codigo,
                materia: this.state.asignatura,
                grado: this.state.singleSelect,
                seccion:this.state.seccion.value,
                titulo:this.state.titulo,
                fecha:this.state.fecha,
                size:this.state.file.size,
                tipo:this.state.file.type,
            };
            let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/guardarRecurso';
            fetch(url, {
                method: 'POST',
                body: JSON.stringify(datadir),
                headers:{
                    'Content-Type': 'application/json'
                },
                Accept: 'application/json',
            })
            .then(async resp  => {
                let nube = (
                  <Alert color="success">
                    <span>Mensaje en la nube</span>
                  </Alert>
                )
                this.setState({subiendo:nube,
                asignatura:null, singleSelect:null});
                await this.verMensajes(this.props.colegio.id);
            })
            .catch(error => {
                console.log(error);
            });
        })
        .catch(err => console.error(err));
    }
  }

  ponerModal = () => {
    const inputValue = this.state.alert;
    this.setState({
      modal: (
        <ReactBSAlert
          custom
          showCancel
          showCloseButton
          confirmBtnText="Modificar"
          cancelBtnText="Salir"
          confirmBtnBsStyle="primary"
          cancelBtnBsStyle="light"
          customIcon="https://raw.githubusercontent.com/djorg83/react-bootstrap-sweetalert/master/demo/assets/thumbs-up.jpg"
          title="Cambios en la Actividad?"
          onConfirm={this.onConfirm}
          onCancel={()=>this.setState({modal:null})}
        >
          <Form className="form-horizontal">
            <Row>
              <Label md="3">Vencimiento</Label>
                <Col md="6">
                  <ReactDatetime
                    inputProps={{
                      className: "form-control",
                      placeholder: "dd-mm-aaaa",
                    }}
                    timeFormat={false}
                    value={this.state.fecha}
                    onChange={(value) =>
                      this.setState({ fecha: value })
                    }
                  />
                </Col>
              </Row>
              <Row>
                <Label md="3">Grado o año</Label>
                <Col md="6">
                  <Select
                    className="react-select primary"
                    classNamePrefix="react-select"
                    name="singleSelect"
                    value={this.state.singleSelect}
                    onChange={(value) =>
                      this.setGrado(value )
                    }
                    options={this.props.Grado}
                    placeholder="Seleccion el Grado o año"
                  />
                  </Col>
                  <Col md="3">
                  <Select
                    className="react-select primary"
                    classNamePrefix="react-select"
                    name="seccion"
                    value={this.state.seccion}
                    onChange={(value) =>
                      this.setSeccion(value )
                    }
                    options={this.props.Seccion}
                    placeholder="Seccion"
                  />
                  </Col>
              </Row>
              <Row>
                <Label md="3">Titulo tarea</Label>
                <Col md="9">
                  <FormGroup>
                    <Input placeholder="Nombre descriptivo de la tarea"
                      type="text"
                      value={this.state.titulo}
                      onChange={(texto) =>
                          this.setState({ titulo:texto.target.value })
                        }
                      />
                  </FormGroup>
                </Col>
              </Row>
              <Row>
                <Label md="3">Asignatura</Label>
                <Col md="9">
                  <Select
                    className="react-select primary"
                    classNamePrefix="react-select"
                    name="singleSelect"
                    value={this.state.asignatura}
                    onChange={(value) =>
                      this.setState({ asignatura: value })
                    }
                    options={this.state.materias}
                    placeholder="Selecciona el Area o Asignatura"
                  />
                </Col>
              </Row>
              <Row>
                <Label md="3">Item Curriculo</Label>
                <Col md="9">
                  <Select
                    className="react-select primary"
                    classNamePrefix="react-select"
                    name="singleSelect"
                    value={this.state.curriculo}
                    onChange={(value) =>
                      this.setState({ curriculo: value })
                    }
                    options={this.state.itemscurriculo}
                    placeholder="Selecciona el Item o Bloque"
                  />
                </Col>
              </Row>
            </Form>
            <Row>.</Row>
            <Row>.</Row>
            <Row>.</Row>
            <Row>.</Row>
        </ReactBSAlert>
      ),
    });
  }

  filtrar=(grado)=>{
    alert(grado);
  }

//   handleChange(e) {
//     this.setState({
//       input: e.target.value
//     })
// }
// submitMessages(e){
//   e.preventDefault();
//   const messagesCopy = this.state.mensajes.slice();
//   messagesCopy.push(this.state.input);
//   this.setState({
//     mensajes: messagesCopy,
//     input: '',
//   });
// }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="6" lg="6" justify="center">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form className="form-horizontal">
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
                          options={this.props.Seccion}
                          placeholder="Seccion"
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Contenido del Mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          <Input placeholder="Nombre descriptivo de la tarea"
                            type="text"
                            value={this.state.input}
                            onChange={(texto) =>
                                this.setState({ input:texto.target.value })
                              }
                            />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Vencimiento</Label>
                      <Col md="6">
                        <ReactDatetime
                          inputProps={{
                            className: "form-control",
                            placeholder: "Date Picker Here",
                          }}
                          timeFormat={false}
                          value={this.state.fecha}
                          onChange={(value) =>
                            this.setState({ fecha: value })
                          }
                        />
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter>
                  <Row>
                    <Col md="9">
                      <Button
                      className="btn-round"
                      color="info" type="submit"
                      onClick={this.submitMessages}
                    >
                        Subir Mensaje
                      </Button>
                    </Col>
                    <Col md="3" />
                      {this.state.mensaje !== null ? (
                      <Col md="9">
                        <Button className="btn-round"
                        color="info" type="submit"
                        onClick={()=>this.uploadFile()}
                      >
                        Subir y registrar tarea
                      </Button>
                    </Col> ) : (null)}
                  </Row>
                </CardFooter>
              </Card>
            </Col>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardText tag="div">
                    <CardTitle tag="h4">Mensajes Enviados</CardTitle>
                  </CardText>
                </CardHeader>
                <CardBody className="table-responsive">
                  <Table className="table-hover">
                    <thead className="text-warning">
                      <tr>
                        <th>#</th>
                        <th>Grado</th>
                        <th>Mensaje</th>
                      </tr>
