  render() {
    return (
      <>
        <div className="content">
          <Row>
            {this.state.modal}
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form onSubmit={enviarDatos} className="form-horizontal">
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
                          options={this.props.Seccion}
                          placeholder="Seccion"
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Label md="3">Contenido del Mensaje</Label>
                      <Col md="9">
                        <FormGroup>
                          <Input placeholder="Asunto del mensaje"
                            type="text"
                            value={this.state.mensaje}
                            onChange={(texto) =>
                              this.setState({ mensaje:texto.target.value })
                            }
                          />
                        </FormGroup>
                      </Col>
                      {this.state.mensaje ? (
                        <Row>
                          <Col md="3" />
                          <Col md="9">
                            <Button className="btn-round"
