import React from "react";

// reactstrap components
import {
  Badge,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Label,
  FormGroup,
  Input,
  Alert,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
} from "reactstrap";
import ReactBSAlert from "react-bootstrap-sweetalert";
import ReactDatetime from "react-datetime";
import defaultImage from "assets/img/image_placeholder.jpg";
import defaultAvatar from "assets/img/placeholder.jpg";
import Select from "react-select";
import * as mainActions from "../actions/mainActions";
import { connect } from 'react-redux';
import { uploadFile } from 'react-s3';

class Mensajes extends React.Component {
  constructor() {
    super();
    this.state = {
      singleSelect: null,
      tareas: [],
      subiendo: null,
      materias:null,
      seccion:null,
      asignatura:null,
      // file: null,
      // imagePreviewUrl: defaultImage,
      titulo:null,
      fecha:null,
      Grado:[],
      Seccion:[],
      Primaria:[],
      Secundaria:[],
      modulos:[],
      itemscurriculo:[],
      curriculo:null,
      acepta:"*",
      parabuscar:null,
      subirRecurso:null,
      mensaje:null,
      enviadas:[],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
  }
  handleClick(e) {
  }
  handleRemove() {
    this.setState({
      file: null,
      imagePreviewUrl: this.props.avatar ? defaultAvatar : defaultImage,
    });
    this.refs.fileInput.value = null;
  }
  setGrado = (value) => {
    this.setState({ singleSelect: value })
    if(value.value>6){
      this.setState({materias:this.props.Secundaria})
    }else{
      this.setState({materias:this.props.Primaria})
    }
  }
  setSeccion = (value) => {
    this.setState({ seccion: value })
  }

  componentDidMount = async () => {
    this.props.verCredenciales();
    if(!this.props.colegio.id){
      this.props.history.push("/auth/login");
    }
    await this.RegistrarMensaje(this.props.colegio.id,"*");
  }

  RegistrarMensaje = async () => {
    const data = {
      grado: this.state.singleSelect,
      seccion:this.state.seccion,
      fecha:this.state.fecha,
      mensaje:this.state.mensaje,
    };
    let url='https://webhooks.mongodb-realm.com/api/client/v2.0/app/aprendemicolegio-kmnsj/service/masterside/incoming_webhook/registrarMensaje';
    fetch(url, {
        method: 'POST', 
        body: JSON.stringify(data),
        headers:{
            'Content-Type': 'application/json'
        },
        Accept: 'application/json',
    })
    .then(async resp  => {
        let nube = (
          <Alert color="success">
            <span>Mensaje registrado</span>
          </Alert>
        )
        this.setState({guardado:nube,
        asignatura:null, singleSelect:null,
        subirRecurso:false, mensaje:null});
        this.props.setData({
          grado:this.state.grado,
          seccion:this.state.seccion,
          fecha:this.state.fecha,
          mensaje:this.state.mensaje});
    })
    .catch(error => {
        console.log(error);
    });
}

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="6" lg="6">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Registro de Mensajes</CardTitle>
                </CardHeader>
                <CardBody>
                  <Form className="form-horizontal">
                    <Row>
                      <Label md="3">Grado o año</Label>
                      <Col md="6">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="singleSelect"
                          value={this.state.singleSelect}
                          onChange={(value) =>
                            this.setGrado(value )
                          }
                          options={this.props.Grado}
                          placeholder="Seleccion el Grado o año"
                        />
                      </Col>
                      <Col md="3">
                        <Select
                          className="react-select primary"
                          classNamePrefix="react-select"
                          name="seccion"
                          value={this.state.seccion}
                          onChange={(value) =>
                            this.setSeccion(value )
                          }
